export default function InfoToolTipPopup({ isOpen, onClose, isSucceeded }) {
  return(
    <section className={`popup popup_type_tooltip ${isOpen ? 'popup_active' : ''}`}>
      <div className="popup__container">
        <div className="popup__tooltip">
          <div 
            className="popup__tooltip-image" 
            style={{backgroundImage: `url(${isSucceeded ? 
              '../../../images/popup__tooltip-image_success.svg' : 
              '../../../images/popup__tooltip-image_fail.svg'})`}}
          />
          <h2 className="popup__tooltip-message" >
            {isSucceeded ? 'Вы успешно зарегистрировались!' : 'Что-то пошло не так! Попробуйте еще раз.'}
          </h2>
        </div>
        <button className="popup__close-button" type="button" onClick={onClose} />
      </div>
    </section>
  )
}
