import { authConfig } from "./constants";

class Auth {
  constructor({ baseUrl, headers }) {
    this._baseUrl = baseUrl;
    this._headers = headers;
  }

  // _showResponseError(responseCode, errors) {
  //   errors.forEach(err => {
  //     if (err.code === responseCode) {
  //       console.log(`Ошибка ${err.code} - ${err.message}`)
  //       return
  //     }
  //   })
  //   console.log(`Ошибка ${responseCode}`)
  // }

  _checkResponse(res) {
    return (res.ok) ? Promise.resolve(res.json()) : Promise.reject(res.status)
  }

  register(email, password) {
    return fetch(`${this._baseUrl}/signup`, {
        method: 'POST',
        headers: this._headers,
        body: JSON.stringify({ email, password })
    })
      .then(this._checkResponse);
  }

  login(email, password) {
    return fetch(`${this._baseUrl}/signin`, {
        method: 'POST',
        headers: this._headers,
        body: JSON.stringify({ email, password })
    })
      .then(this._checkResponse);
  }

  tokenCheck(token) {
    return fetch(`${this._baseUrl}/users/me`, {
        method: 'GET',
        headers: {
          "Content-Type": "application/json",
          "Authorization" : `Bearer ${token}`
        }
    })
      .then(this._checkResponse);
  }
}

export default new Auth(authConfig);
